import React from 'react';
import { AppBar, Toolbar, Typography, Box, Chip, Avatar } from '@mui/material';
import { LockOutlined } from '@mui/icons-material';

const Header = ({ currentUser }) => {
  return (
    <AppBar position="static" color="primary" elevation={0} sx={{ mb: 3 }}>
      <Toolbar>
        <LockOutlined sx={{ mr: 2 }} />
        <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
          PGP Secure Messenger
        </Typography>

        {currentUser && (
          <Chip
            avatar={<Avatar>{currentUser.name.charAt(0)}</Avatar>}
            label={`Active: ${currentUser.name}`}
            color="secondary"
            variant="outlined"
            sx={{ 
              bgcolor: 'rgba(255,255,255,0.1)', 
              borderColor: 'rgba(255,255,255,0.3)',
              color: 'white'
            }}
          />
        )}
      </Toolbar>
    </AppBar>
  );
};

export default Header;
