import React from 'react';
import { 
  AppBar, 
  Toolbar, 
  Typography, 
  Box, 
  Chip, 
  Avatar, 
  IconButton, 
  Tooltip 
} from '@mui/material';
import { 
  LockOutlined, 
  Brightness4, 
  Brightness7 
} from '@mui/icons-material';

const Header = ({ currentUser, toggleTheme, themeMode }) => {
  return (
    <AppBar position="static" elevation={1} sx={{ mb: 0 }}>
      <Toolbar>
        <Box sx={{ display: 'flex', alignItems: 'center' }}>
          <LockOutlined sx={{ mr: 1.5 }} />
          <Typography variant="h6" component="div" sx={{ fontWeight: 600 }}>
            PGP Secure Messenger
          </Typography>
        </Box>

        <Box sx={{ flexGrow: 1 }} />

        <Tooltip title={`Switch to ${themeMode === 'light' ? 'dark' : 'light'} mode`}>
          <IconButton onClick={toggleTheme} color="inherit" sx={{ mr: 2 }}>
            {themeMode === 'light' ? <Brightness4 /> : <Brightness7 />}
          </IconButton>
        </Tooltip>

        {currentUser && (
          <Chip
            avatar={<Avatar sx={{ bgcolor: 'secondary.main' }}>{currentUser.name.charAt(0)}</Avatar>}
            label={`${currentUser.name}`}
            variant="outlined"
            sx={{ 
              bgcolor: 'rgba(255,255,255,0.1)', 
              borderColor: 'rgba(255,255,255,0.3)',
              color: 'white',
              fontWeight: 500
            }}
          />
        )}
      </Toolbar>
    </AppBar>
  );
};

export default Header;
