import React from 'react';
import { Grid, Paper, Box, Typography, Alert } from '@mui/material';
import Inbox from './Inbox';
import KeyManager from './KeyManager';
import Compose from './Compose';
import '../../styles/Dashboard.css'; // Fix path to CSS

const Dashboard = ({ 
  currentUser, 
  updateUserKeyPair, 
  messages, 
  addMessage, 
  users, 
  selectedRecipient 
}) => {
  // Display message if no user is selected
  if (!currentUser) {
    return (
      <Alert severity="info" sx={{ mt: 4 }}>
        Please select a user from the sidebar to start using the application.
      </Alert>
    );
  }

  return (
    <Grid container spacing={3}>
      <Grid item xs={12} md={4}>
        <Paper elevation={2} sx={{ p: 2, height: '100%', bgcolor: '#e8f5e9' }}>
          <Typography variant="h5" gutterBottom>Inbox</Typography>
          <Inbox 
            messages={messages}
            currentUser={currentUser}
            keyPair={{
              privateKey: currentUser.privateKey,
              publicKey: currentUser.publicKey
            }}
          />
        </Paper>
      </Grid>
      <Grid item xs={12} md={4}>
        <Paper elevation={2} sx={{ p: 2, height: '100%', bgcolor: '#fff3e0' }}>
          <Typography variant="h5" gutterBottom>Key Manager</Typography>
          <KeyManager 
            currentUser={currentUser}
            updateUserKeyPair={updateUserKeyPair}
          />
        </Paper>
      </Grid>
      <Grid item xs={12} md={4}>
        <Paper elevation={2} sx={{ p: 2, height: '100%', bgcolor: '#e3f2fd' }}>
          <Typography variant="h5" gutterBottom>Compose</Typography>
          <Compose 
            currentUser={currentUser}
            selectedRecipient={selectedRecipient}
            addMessage={addMessage}
            keyPair={{
              privateKey: currentUser.privateKey,
              publicKey: currentUser.publicKey
            }}
          />
        </Paper>
      </Grid>
    </Grid>
  );
};

export default Dashboard;
