import React, { useState } from 'react';
import { 
  Box, 
  Typography, 
  List, 
  ListItem,
  ListItemButton,
  ListItemText,
  ListItemAvatar,
  Avatar,
  Divider,
  Paper,
  Badge,
  Tooltip,
  IconButton,
  useTheme,
  alpha
} from '@mui/material';
import { Person, PersonOutline, CheckCircle } from '@mui/icons-material';

const Sidebar = ({ users, currentUser, setCurrentUser, selectedRecipient, setSelectedRecipient }) => {
  const theme = useTheme();
  const [hoveredUser, setHoveredUser] = useState(null);

  return (
    <Paper 
      elevation={theme.palette.mode === 'light' ? 1 : 0}
      sx={{ 
        width: 280,
        height: '100%',
        borderRadius: '0 0 0 0',
        borderRight: `1px solid ${theme.palette.divider}`,
        display: 'flex',
        flexDirection: 'column',
        bgcolor: theme.palette.mode === 'light' 
          ? alpha(theme.palette.background.paper, 0.9)
          : alpha(theme.palette.background.paper, 0.4),
        backgroundImage: theme.palette.mode === 'light'
          ? 'linear-gradient(to bottom, rgba(240, 242, 255, 0.8), rgba(255, 255, 255, 0.9))'
          : 'none',
        backdropFilter: 'blur(8px)',
      }}
    >
      <Box sx={{ p: 3, pb: 2 }}>
        <Typography 
          variant="subtitle1" 
          sx={{ 
            mb: 0.5, 
            fontWeight: 700,
            color: theme.palette.mode === 'light' 
              ? theme.palette.primary.main 
              : theme.palette.primary.light
          }}
        >
          SELECT YOUR IDENTITY
        </Typography>
        <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
          Choose which user you want to be
        </Typography>
      </Box>

      <List sx={{ px: 2 }}>
        {users.map((user) => (
          <ListItem key={user.id} disablePadding sx={{ mb: 0.5 }}>
            <ListItemButton
              selected={currentUser?.id === user.id}
              onClick={() => setCurrentUser(user)}
              onMouseEnter={() => setHoveredUser(user.id)}
              onMouseLeave={() => setHoveredUser(null)}
              sx={{
                borderRadius: 2,
                bgcolor: currentUser?.id === user.id
                  ? theme.palette.mode === 'light'
                    ? alpha(theme.palette.primary.main, 0.1)
                    : alpha(theme.palette.primary.main, 0.2)
                  : 'transparent',
                transition: 'all 0.2s',
                '&:hover': {
                  bgcolor: currentUser?.id === user.id
                    ? theme.palette.mode === 'light'
                      ? alpha(theme.palette.primary.main, 0.15)
                      : alpha(theme.palette.primary.main, 0.25)
                    : theme.palette.mode === 'light'
                      ? alpha(theme.palette.action.hover, 0.8)
                      : alpha(theme.palette.action.hover, 0.3),
                  transform: 'translateX(3px)'
                },
                '&.Mui-selected': {
                  bgcolor: theme.palette.mode === 'light'
                    ? alpha(theme.palette.primary.main, 0.1)
                    : alpha(theme.palette.primary.main, 0.2),
                }
              }}
            >
              <ListItemAvatar>
                <Avatar
                  sx={{
                    bgcolor: currentUser?.id === user.id
                      ? theme.palette.primary.main
                      : theme.palette.mode === 'light'
                        ? '#e0e0e0'
                        : 'rgba(255,255,255,0.1)',
                    color: currentUser?.id === user.id
                      ? 'white'
                      : theme.palette.text.primary,
                    transition: 'all 0.2s',
                    transform: hoveredUser === user.id ? 'scale(1.1)' : 'scale(1)',
                    boxShadow: currentUser?.id === user.id
                      ? `0 0 0 2px ${theme.palette.background.paper}, 0 0 0 4px ${alpha(theme.palette.primary.main, 0.3)}`
                      : 'none'
                  }}
                >
                  {user.name.charAt(0)}
                </Avatar>
              </ListItemAvatar>
              <ListItemText
                primary={user.name}
                secondary={user.email}
                primaryTypographyProps={{
                  fontWeight: currentUser?.id === user.id ? 600 : 400,
                  color: currentUser?.id === user.id 
                    ? theme.palette.primary.main
                    : theme.palette.text.primary
                }}
              />
            </ListItemButton>
          </ListItem>
        ))}
      </List>

      <Divider />

      {currentUser && (
        <>
          <Box sx={{ p: 3, pb: 2 }}>
            <Typography 
              variant="subtitle1" 
              sx={{ 
                mb: 0.5, 
                fontWeight: 700,
                color: theme.palette.mode === 'light' 
                  ? theme.palette.primary.main 
                  : theme.palette.primary.light
              }}
            >
              SELECT RECIPIENT
            </Typography>
            <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
              Choose the recipient of your message
            </Typography>
          </Box>

          <List sx={{ px: 2 }}>
            {users.filter(user => user.id !== currentUser.id).map((user) => (
              <ListItem key={user.id} disablePadding sx={{ mb: 0.5 }}>
                <ListItemButton
                  selected={selectedRecipient?.id === user.id}
                  onClick={() => setSelectedRecipient(user)}
                  onMouseEnter={() => setHoveredUser(user.id)}
                  onMouseLeave={() => setHoveredUser(null)}
                  sx={{
                    borderRadius: 2,
                    bgcolor: selectedRecipient?.id === user.id
                      ? theme.palette.mode === 'light'
                        ? alpha(theme.palette.primary.main, 0.1)
                        : alpha(theme.palette.primary.main, 0.2)
                      : 'transparent',
                    transition: 'all 0.2s',
                    '&:hover': {
                      bgcolor: selectedRecipient?.id === user.id
                        ? theme.palette.mode === 'light'
                          ? alpha(theme.palette.primary.main, 0.15)
                          : alpha(theme.palette.primary.main, 0.25)
                        : theme.palette.mode === 'light'
                          ? alpha(theme.palette.action.hover, 0.8)
                          : alpha(theme.palette.action.hover, 0.3),
                      transform: 'translateX(3px)'
                    },
                    '&.Mui-selected': {
                      bgcolor: theme.palette.mode === 'light'
                        ? alpha(theme.palette.primary.main, 0.1)
                        : alpha(theme.palette.primary.main, 0.2),
                    }
                  }}
                >
                  <ListItemAvatar>
                    <Avatar
                      sx={{
                        bgcolor: selectedRecipient?.id === user.id
                          ? theme.palette.primary.main
                          : theme.palette.mode === 'light'
                            ? '#e0e0e0'
                            : 'rgba(255,255,255,0.1)',
                        color: selectedRecipient?.id === user.id
                          ? 'white'
                          : theme.palette.text.primary,
                        transition: 'all 0.2s',
                        transform: hoveredUser === user.id ? 'scale(1.1)' : 'scale(1)',
                        boxShadow: selectedRecipient?.id === user.id
                          ? `0 0 0 2px ${theme.palette.background.paper}, 0 0 0 4px ${alpha(theme.palette.primary.main, 0.3)}`
                          : 'none'
                      }}
                    >
                      {user.name.charAt(0)}
                    </Avatar>
                  </ListItemAvatar>
                  <ListItemText
                    primary={user.name}
                    secondary={user.email}
                    primaryTypographyProps={{
                      fontWeight: selectedRecipient?.id === user.id ? 600 : 400,
                      color: selectedRecipient?.id === user.id 
                        ? theme.palette.primary.main
                        : theme.palette.text.primary
                    }}
                  />
                </ListItemButton>
              </ListItem>
            ))}
          </List>
        </>
      )}
    </Paper>
  );
};

export default Sidebar;
